import React from 'react';
import { Link } from 'react-router-dom';
import DeleteBook from './DeleteBook';

function BooksSection({ data }) {
    const Delete = () => {
        // Delete functionality
    };

    return (
        <div className='row row-cols-1 row-cols-md-3 g-4 justify-content-center'>
            {data && data.map((item, index) => (
                <div key={index} className='col'>
                    <div className='card h-100'>
                        <img src={item.image} className='card-img-top' alt='Book Cover' style={{ height: '300px', width:'300px' }} />
                        <div className='card-body'>
                            <h5 className='card-title'>{item.bookname.slice(0, 20)}...</h5>
                            <p className='card-text'>{item.description}</p>
                            <p className='card-text'><small className='text-muted'>Author: {item.author}</small></p>
                            <p className='card-text'><small className='text-muted'>Genre: {item.genre}</small></p>
                            <h5 className='card-text'>Price: <span className='text-danger'>$ {item.price}</span></h5>
                        </div>
                        <div className='card-footer'>
                            <div className='d-flex justify-content-between align-items-center'>
                                <Link to={`/update/${item._id}`} className='btn btn-primary'>UPDATE</Link>
                                <DeleteBook id={item._id} onClick={Delete} />
                            </div>
                        </div>
                    </div>
                </div>
            ))}
        </div>
    );
}

export default BooksSection;
