import React from 'react';
import { Link } from 'react-router-dom';
import DeleteBook from './DeleteBook';

function BooksSection({ data }) {
    const Delete = () => {
        // Delete functionality
    };

    return (
        <div className='d-flex justify-content-around align-items-center flex-wrap '>
            {data && data.map((item, index) => (
                <div key={index} className='m-3' style={{ width: "200px", height: "350px", border: '1px solid white', borderRadius: '20px' }}>
                    <div>
                        <img style={{ width: '200px', height: '210px', borderTopLeftRadius: '20px', borderTopRightRadius: '20px' }} className='img-fluid' src={item.image} alt='/' />
                    </div>
                    <h6 style={{ fontSize: "15px" }} className='px-2 my-1 text-white'>{item.bookname.slice(0, 20)}...</h6>
                    <p className='px-2 my-1 text-white'></p>
                    <b style={{ fontSize: "20px", color: 'red' }} className='mb-0 px-2'> $ {item.price}</b>
                    <div className='d-flex justify-content-around align-items-center my-2'>
                        <Link to={`/update/${item._id}`} className='btn btn-primary'>UPDATE</Link> {/* Link to UpdateBook component */}
                        <DeleteBook id={item._id} onClick={Delete} />
                    </div>
                </div>
            ))}
        </div>
    );
}

export default BooksSection;
