import React from 'react';
import { Link } from 'react-router-dom';
import DeleteBook from './DeleteBook';

function BooksSection({ data }) {
    const Delete = () => {
        // Delete functionality
    };

    return (
        <div className='d-flex justify-content-around align-items-center flex-wrap'>
            {data && data.map((item, index) => (
                <div key={index} className='card m-3' style={{ width: "200px", height: "350px", borderRadius: '20px', boxShadow: '0 4px 8px rgba(0,0,0,0.1)' }}>
                    <img className='card-img-top' style={{ height: '210px', borderTopLeftRadius: '20px', borderTopRightRadius: '20px' }} src={item.image} alt='Book Cover' />
                    <div className='card-body'>
                        <h6 className='card-title' style={{ fontSize: "15px", color: '#333' }}>{item.bookname.slice(0, 20)}...</h6>
                        <p className='card-text' style={{ fontSize: "14px", color: '#666' }}>{item.description}</p>
                        <p className='card-text' style={{ fontSize: "14px", color: '#666' }}>Author: {item.author}</p>
                        <p className='card-text' style={{ fontSize: "14px", color: '#666' }}>Genre: {item.genre}</p>
                        <b style={{ fontSize: "20px", color: 'red' }} className='mb-0'> $ {item.price}</b>
                        <div className='d-flex justify-content-around align-items-center my-2'>
                            <Link to={`/update/${item._id}`} className='btn btn-primary'>UPDATE</Link>
                            <DeleteBook id={item._id} onClick={Delete} />
                        </div>
                    </div>
                </div>
            ))}
        </div>
    );
}

export default BooksSection;
