import React from 'react'

function BooksSection({data}) {
    console.log(data);
  return (
    <div className='d-flex justify-content-around align-items-center flex-wrap my-3'>
        {data && data.map((item,index)=> (
        <div className='' style={{width:"200px", height:"400px", backgroundColor:"orange"}}>
            <div>
                <img style={{width:'200px', height:'210px'}} className='img-fluid' src={item.image} alt='/'/>
            </div>
            <h6 style={{fontSize:"15px"}} className='px-2 m-0'>{item.bookname}.slice(0,)</h6>
            <p style={{fontSize:"20px"}} className='m-0 px-2'> $. {item.price}</p>
            </div>
            ))}
    </div>
  )
}

export default BooksSection;
